import React from 'react';
import { makeStyles } from '@material-ui/core';
import { ABCLogo, HRCLogo, SearchIcon, EditIcon } from '../assets'
import { pxToRem } from '../utils/theme';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@material-ui/core';
import { AppBar, Toolbar, Button, Input, InputAdornment } from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
    LandingPage: {
        display: 'flex',
        flexDirection: 'column',
        // border: '1px solid black',
    },
    Header: {
        display: 'flex',
        paddingBlock: '5px',
        width: 1553.620,
        // paddinLeft: '20px'
        // border: '2px solid black',
    },
    ABCContainer: {
        display: 'flex',
    },
    ABCLogo: {
        top: pxToRem('20px'),
        left: pxToRem('30px'),
        width: pxToRem('44px'),
        height: pxToRem('46px'),
        paddingLeft: '5px',
        opacity: 1,
        fill: '#CD7925',
        // background: '#CD7925',
        // paddingTop: pxToRem('10px'),
        // background: '#CD7925 0% 0% no-repeat padding-box',
    },
    ABCText: {
        font: 'Futura PT',
        fontSize: '39px',
        fontWeight: 'bold',
        color: '#FFFFFF',
        paddingLeft: '10px',
        verticalAlign: 'center',
        // display: 'flex',
    },
    HRCLogo: {
        position: 'absolute',
        top: '0%',
        // width: '100%',
        textAlign: 'center',
        paddingBlock: '10px',
        // border: '2px solid black',
        width: window.innerWidth - 40,
    },
    InvoiceList: {
        font: 'Ubuntu',
        fontSize: '20px',
        color: '#FFFFFF',
        // paddingInline: '10px',
        paddingTop: '10px',
        paddingBottom: '20px',
        paddingLeft: '20px',
        width: '120px',
        // top: '100px',
