import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core';
import { AppBar, Toolbar, Button, Select, MenuItem } from '@material-ui/core';
import { Card, CardContent } from '@material-ui/core';
import { CloseIcon } from '../assets';

const useStyles = makeStyles((theme) => ({
    WindowHeader: {
        background: '#2A3E4C',
        borderRadius: '10px 10px 0px 0px',
        width: window.innerWidth - 160,
        position: 'static',
    },
    Title: {
        color: '#FFFFFF',        
    },
    TopRightMenu: {
        broder: '2px solid white',
        position: 'fixed',
        right: '100px',
        display: 'flex'
    },
    CloseButton: {
        paddingLeft: '0px',
        paddingRight: '0px',
        width: '25px',
        height: '25px',
    },
    Dropdown: {
        width: '180px',
        color: '#FFFFFF',
        background: '#283A46',
        border: '1px solid #356680',
        borderRadius: 10,
        paddingLeft: '20px',
        paddingTop: '3px',
        paddingBottom: '3px',
    },
    ViewText: {
        paddingTop: '18px', 
        paddingRight: '15px',
        letterSpacing: '0.33px',
        color: '#C0C6CA',
    },
    Window: {
        width: window.innerWidth - 160,
        height: window.innerHeight - 220,
        borderRadius: '0px 0px 10px 10px',
        background: '#2A3E4C',
    },
    Body: {
        fontSize: '15px'
    },
}));

const WindowHeader = () => {
    const classes = useStyles();
    const [ template, setTemplate ] = React.useState('');
    const handleChange = (event) => {
        setTemplate(event.target.value);
    }
    return (
        <AppBar className={classes.WindowHeader}>
            <Toolbar>
                <div className={classes.Title}>
                    View Correspondence (2)
                </div>
                <div className={classes.TopRightMenu}>
                    <div className={classes.ViewText}>
                        View:
                    </div>
                    <div style={{ paddingTop: '10px', paddingBottom: '10px' }}>
                        <Select 
                            className={classes.Dropdown} 
                            disableUnderline={true}
                            onChnage={handleChange}
                            defaultValue={"Template 1"}
                            // value={template}
                        >
                            <MenuItem value="Template 1">
                                <div>Template 1</div>
                            </MenuItem>
                            <MenuItem value="Template 2">
                                Template 2
                            </MenuItem>
                        </Select>
                    </div>
                    <div style={{ paddingTop: '17px', paddingBottom: '10px', paddingLeft: '20px' }}>
                        <Button className={classes.CloseButton}><CloseIcon/></Button>
                    </div>
                </div>
            </Toolbar>
        </AppBar>
    );
}

const Window = () => {
    const classes = useStyles();
    return (
        <Card className={classes.Window}>
            <CardContent className={classes.Body}>
                abc
            </CardContent>
        </Card>
    );
}

const ViewCorrespondencePage = () => {
    return (
        <div>
            <div style = {{ paddingLeft: '80px', paddingTop: '80px' }}>
                <WindowHeader/>
            </div>
            <div style = {{ paddingLeft: '80px' }}>
                <Window/>
            </div>
        </div>
    );
}

export default ViewCorrespondencePage;
