import React from 'react';
import { makeStyles } from '@material-ui/core';
import { Dialog, IconButton, Button, Input } from '@material-ui/core';
import MuiDialogTitle from '@material-ui/core/DialogTitle';
import MuiDialogContent from '@material-ui/core/DialogContent';
import MuiDialogActions from '@material-ui/core/DialogActions';
import { CloseIcon } from '../assets';

const useStyles = makeStyles((theme) => ({
    dialogPaper: {
        minHeight: '60vh', 
        maxHeight: '60vh',
        minWidth: '60vw',
        maxWidth: '60vw'
    },
    WindowHeader: {
        background: '#2A3E4C',
        borderRadius: '10px 10px 0px 0px',
        position: 'static',
    },
    Title: {
        color: '#FFFFFF',   
        font: 'Ubuntu',
        fontWeight: 'normal',
    },
    TopRightMenu: {
        position: 'fixed',
        top: '160px',
        right: '330px',
    },
    Body: {
        fontSize: '15px',
        color: '#97A1A9',
        background: '#2A3E4C',
        paddingBlock: '40px',
        border: '2px solid white',
    },
    Column1: {
        border: '2px solid yellow',
        width: '30vw',
        height: '30vh',
    },
    Column2: {
        border: '2px solid red',
        width: '30vw',
        height: '30vh',
    },
    InputBoxLeft: {
        color: '#97A1A9',
        // borderBottom: 'none',
        border: '1px solid #356680',
        background: '#283A46',
    },
}))

const AddMenu = () => {
    const classes = useStyles();
    return (
        <div style={{ display: 'flex' }}>
            <div className={classes.Column1}>
                <div style={{ display: 'flex' }}>
                    <div>
                        <div>
                            Customer Name
                        </div>
                    </div>
                    <div>
                        <Input
                            className={classes.InputBoxLeft}
                            disableUnderline={true}
                        >
                        </Input>
                    </div>
                </div>
            </div>
            <div className={classes.Column2}>

            </div>
        </div>
    )
}

const Window = () => {
    const classes = useStyles();
    const [ open, setOpen ] = React.useState(true);
    const [ maxWidth ] = React.useState('lg');
    const [ fullWidth ] = React.useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    }

    const handleClose = () => {
        setOpen(false);
    }

    return (
        <Dialog onCLose={handleClose} open={open} maxWidth={maxWidth} fullWidth={fullWidth} classes={{ paper: classes.dialogPaper }}>
            <MuiDialogTitle className={classes.WindowHeader}>
                <div style={{ display: 'flex' }}>
                    <div className={classes.Title}>
                        Add Invoice
                    </div>
                    <div className={classes.TopRightMenu}>
                        <IconButton onClick={handleClose}>
                            <CloseIcon/>
                        </IconButton>
                    </div>
                </div>
            </MuiDialogTitle>
            <MuiDialogContent className={classes.Body}>
                <AddMenu/>
            </MuiDialogContent>
        </Dialog>
    )
}

const AddInvoicePage = () => {
    return (
        <div>
            <Window/>
        </div>
    )
}

export default AddInvoicePage;
