];

class MuiVirtualizedTable extends React.PureComponent {
    static defaultProps = {
        headerHeight: 48,
        rowHeight: 48,
    };
  
    getRowClassName = ({ index }) => {
        const { classes, onRowClick } = this.props;
    
        return clsx(classes.tableRow, classes.flexContainer, {
            [classes.tableRowHover]: index !== -1 && onRowClick != null,
        });
    };
  
    cellRenderer = ({ cellData, columnIndex }) => {
        const { columns, classes, rowHeight, onRowClick } = this.props;
        return (
            <TableCell
                component="div"
                className={clsx(classes.tableCell, classes.flexContainer, {
                    [classes.noClick]: onRowClick == null,
                })}
                variant="body"
                style={{ height: rowHeight }}
                align={(columnIndex != null && columns[columnIndex].numeric) || false ? 'right' : 'left'}
            >
                {cellData}
            </TableCell>
        );
    };
  
    headerRenderer = ({ label, columnIndex }) => {
        const { headerHeight, columns, classes } = this.props;
  
        return (
            <TableCell
                component="div"
                className={clsx(classes.tableCell, classes.flexContainer, classes.noClick)}
                variant="head"
                style={{ height: headerHeight }}
                align={columns[columnIndex].numeric || false ? 'right' : 'left'}
            >
                <span>{label}</span>
            </TableCell>
        );
    };

    render() {
        const { classes, columns, rowHeight, headerHeight, ...tableProps } = this.props;
        return (
            <AutoSizer>
                {({ height, width }) => (
                    <Table
                        height={height}
                        width={width}
                        rowHeight={rowHeight}
                        gridStyle={{
                        direction: 'inherit',
                        }}
                        headerHeight={headerHeight}
                        className={classes.table}
                        {...tableProps}
                        rowClassName={this.getRowClassName}
                    >
                        {columns.map(({ dataKey, ...other }, index) => {
                        return (
                            <Column
                            key={dataKey}
                            headerRenderer={(headerProps) =>
                                this.headerRenderer({
                                ...headerProps,
                                columnIndex: index,
                                })
                            }
                            className={classes.flexContainer}
                            cellRenderer={this.cellRenderer}
                            dataKey={dataKey}
                            {...other}
                            />
                        );
                        })}
                    </Table>
                )}
            </AutoSizer>
        );
    }
}

const DataTable = (props) => {
    const classes = useStyles();
    return (
        <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="simple table">
                <TableHead>
                    <TableRow>
                        <TableCell>Customer Name</TableCell>
                        <TableCell>Customer #</TableCell>
                        <TableCell>Invoice #</TableCell>
                        <TableCell>Invoice Amount</TableCell>
                        <TableCell>Due Date</TableCell>
                        <TableCell>Predicted Payment Date</TableCell>
                        <TableCell>Predicted Aging Bucket</TableCell>
                        <TableCell>Notes</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {rows.map((row) => (
                        <TableRow key={row.invoiceNumber}>
                        <TableCell component="th" scope="row">{row.customerName}</TableCell>
                        <TableCell align="right">{row.customerNumber}</TableCell>
                        <TableCell align="right">{row.invoiceNumber}</TableCell>
                        <TableCell align="right">{row.invoiceAmount}</TableCell>
                        <TableCell align="right">{row.dueDate}</TableCell>
                        <TableCell align="right">{row.predictedPaymentDate}</TableCell>
                        <TableCell align="right">{row.predictedAgingBucket}</TableCell>
                        <TableCell align="right">{row.notes}</TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </TableContainer>
    )
}

const LandingPage = (props) => {
    const classes = useStyles();
    return (
        <div className={classes.LandingPage}>
            <div>
