import React from 'react';
import { makeStyles } from '@material-ui/core';
import { ReactComponent as ABCLogo } from '../assets/companyLogo.svg';
import { ReactComponent as HRCLogo } from '../assets/hrcLogo.svg';
import { pxToVh, pxToRem } from '../utils/theme';
import { CenterFocusStrong } from '@material-ui/icons';

const useStyles = makeStyles((theme) => ({
    LandingPage: {
        display: 'flex',
        flexDirection: 'column',
    },
    Header: {
        display: 'flex',
        paddingBlock: '5px',
    },
    ABCCOntainer: {
        display: 'flex',
    },
    ABCLogo: {
        top: pxToRem('20px'),
        left: pxToRem('30px'),
        width: pxToRem('44px'),
        height: pxToRem('46px'),
        paddingLeft: '5px',
        // paddingTop: pxToRem('10px'),
        // background: '#CD7925 0% 0% no-repeat padding-box',
    },
    ABCText: {
        font: 'Futura PT',
        fontSize: '39px',
        fontWeight: 'bold',
        color: '#FFFFFF',
        paddingLeft: '10px',
        verticalAlign: 'center',
        // display: 'flex',
    },
    HRCLogo: {
        position: 'absolute',
        top: '0%',
        width: '100%',
        textAlign: 'center',
        paddingBlock: '10px',
    },
    InvoiceList: {
        font: 'Ubuntu',
        fontSize: '20px',
        color: '#FFFFFF',
        paddingInline: '10px',
        // top: '100px',
        // left: '30px',
        // width: '141px',
        // height: '31px',
    },
}));

const Header = (props) => {
    const classes = useStyles();
    return (
        <div className={classes.Header}>
            <div className={classes.ABCCOntainer}>
                <div className={classes.ABCLogo}>
                    <ABCLogo/> 
                </div>
                <div className={classes.ABCText}>
                    ABC Products
                </div>
            </div>
            <div className={classes.HRCLogo}>
                <HRCLogo/>
            </div>
        </div>
    )
}

const Table = (props) => {
    return (
        <div>

        </div>
    )
}

const LandingPage = (props) => {
    const classes = useStyles();
    return (
        <div className={classes.LandingPage}>
            <div>
                <Header/>
            </div>
            <div className={classes.InvoiceList}>
                Invoice List
            </div>
            <div>
                <Table/>
            </div>
        </div>
    )
}

export default LandingPage;
