import React from 'react';
import { makeStyles } from '@material-ui/core';
import { ReactComponent as ABCLogo } from '../assets/ABCLogoFull.svg';
import { ReactComponent as HRCLogo } from '../assets/hrcLogo.svg';
import { ReactComponent as searchIcon } from '../assets/searchIcon.svg'
import { pxToRem } from '../utils/theme';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import { AppBar, Toolbar, Button, TextField, Input } from '@material-ui/core'

const useStyles = makeStyles((theme) => ({
    LandingPage: {
        display: 'flex',
        flexDirection: 'column',
        // borde
        left: pxToRem('30px'),
        width: pxToRem('44px'),
        height: pxToRem('46px'),
        paddingLeft: '5px',
        opacity: 1,
        // background: '#CD7925',
        // paddingTop: pxToRem('10px'),
        // background: '#CD7925 0% 0% no-repeat padding-box',
    },
    ABCText: {
        font: 'Futura PT',
        fontSize: '39px',
        fontWeight: 'bold',
        color: '#FFFFFF',
        paddingLeft: '10px',
        verticalAlign: 'center',
        // display: 'flex',
    },
    HRCLogo: {
        position: 'absolute',
        top: '0%',
        // width: '100%',
        textAlign: 'center',
        paddingBlock: '10px',
        // border: '2px solid black',
        width: window.innerWidth - 40,
    },
    InvoiceTable: {
        // display: 'flex',
        // flexDirection: 'column',
        // top: '20%',
        // border: '10px solid black',
        // top: '161px',
        // left: '30px',
        // width: '90%',
        // height: '759.991px',
        // border: '10px solid black',
        // border: '10px solid black',
    },
    ToolBar: {
        // top: '2s0%',
        display: 'flex',
        // width: '1603.620px',
        // width: '80%',
        // height: 600, 
        // width: 1553.620,
        width: window.innerWidth - 40,
        position: 'static',
        background: '#273D49CC',
        boxShadow: 'none', 
        borderBottom: 'none',
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,
    },
    DataTable: {
        // width: '99%',
        // border: '10px solid black',
        paddingLeft: '20px',
        // padding
        borderBottomLeftRadius: 10,
        borderBottomRightRadius: 10,
    },
    tableHeading: {
        backgroundColor: '#273D49CC',
        color: '#97A1A9',
        fontSize: '15px',
        borderBottom: 'none',
        opacity: '100%',
        
    },
    tableBody: {
        "&:nth-of-type(odd)": {
            backgroundColor: '#273D49CC',
        },
        "&:nth-of-type(even)": {
            backgroundColor: "#283A46"
        },
        color: '#FFFFFF',
        fontSize: '16px',
    },
    tableRow: {
        color: '#FFFFFF',
        borderBottom: 'none',
    },
    PredictButton: {
        color: '#FFFFFF',
        background: '#97A1A9',
        // font: 'Ubuntu',
        // fontFamily: 'normal',
        textTransform: 'none',
        paddingLeft: '10px',
        paddingRight: '10px',
        height: '45px',
        borderRadius: 10,
    },
    ViewCorrespondence: {
        color: '#97A1A9',
        background: 'transparent',
        border: '1px solid #97A1A9',
        borderRadius: 10,
        textTransform: 'none',
        height: '45px',
    },
    searchByInvoiceNumber: {
        color: '#97A1A9',
        borderBottom: 'none',
        border: '1px solid #356680',
        background: '#283A46',
        borderRadius: 10,
        paddingLeft: '10px',
        paddingRight: '84px',
        disableUnderline: true,
        height: '45px',
        borderBottom: '1px solid #356680',
        width: '340px',
    },
    DeleteButton: {
        color: '#97A1A9',
        border: '1px solid #97A1A9',
        borderRadius: 10,
        textTransform: 'none',
        height: '45px'
    },
}));

const Header = (props) => {
    const classes = useStyles();
    return (
        <div className={classes.Header}>
            <div className={classes.ABCContainer}>
                <div className={classes.ABCLogo}>
                    <ABCLogo/> 
                </div>
                {/* <div className={classes.ABCText}>
                    ABC Products
                </div> */}
            </div>
            <div className={classes.HRCLogo}>
                <HRCLogo/>
            </div>
        </div>
    )
}

function createData(customerName, customerNumber, invoiceNumber, invoiceAmount, dueDate, predictedPaymentDate, predictedAgingBucket, notes) {
    return { customerName, customerNumber, invoiceNumber, invoiceAmount, dueDate, predictedPaymentDate, predictedAgingBucket, notes };
}

const rows = [
    createData('Andrea James', 2523532, 73457346735, '122.87K', '23-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Jessica Joe', 3523312, 54723243652, '1.87K', '15-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Teresa Hawkins', 9888757, 76531467365, '22.87K', '03-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Dominic White', 4523426, 57635634655, '55.70K', '22-Mar-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Andrea James', 2523532, 73457346735, '122.87K', '23-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Jessica Joe', 3523312, 54723243652, '1.87K', '15-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Teresa Hawkins', 9888757, 76531467365, '22.87K', '03-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Dominic White', 4523426, 57635634655, '55.70K', '22-Mar-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Andrea James', 2523532, 73457346735, '122.87K', '23-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Jessica Joe', 3523312, 54723243652, '1.87K', '15-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Teresa Hawkins', 9888757, 76531467365, '22.87K', '03-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Dominic White', 4523426, 57635634655, '55.70K', '22-Mar-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Andrea James', 2523532, 73457346735, '122.87K', '23-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Jessica Joe', 3523312, 54723243652, '1.87K', '15-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Teresa Hawkins', 9888757, 76531467365, '22.87K', '03-Jan-2021', '--', '--', 'Lorem Ipsum dolor...'),
    createData('Dominic White', 4523426, 57635634655, '55.70K', '22-Mar-2021', '--', '--', 'Lorem Ipsum dolor...'),
];

const Bar = (props) => {
    const classes = useStyles();
    return (
        <AppBar className={classes.ToolBar}>
            <Toolbar style={{ display: 'flex' }}>
                <div style={{ display: 'flex' }}>
                    <div style={{ paddingRight: '20px', paddingTop: '10px', }}>
                        <Button className={classes.PredictButton}>Predict</Button>
                    </div>
                    <div style={{ paddingRight: '10px', paddingTop: '10px', }}>
                        <Button className={classes.ViewCorrespondence}>View Correspondence</Button>
                    </div>
                </div>
                <div style={{ position: 'fixed', right: 40, display: 'flex' }}>
                    <div style={{ paddingRight: '20px', paddingTop: '10px', }}>
                        <Button className={classes.DeleteButton}>— Delete</Button>
                    </div>
                    <div style={{ paddingTop: '10px', }}>
                        <Input
                            className={classes.searchByInvoiceNumber}
                            placeholder='Search by Invoice Number'
                            disableUnderline={true}
                        ></Input>
                    </div>
                </div>
            </Toolbar>
        </AppBar>
    )
}

const LandingPage = (props) => {
    const classes = useStyles();
    return (
        <div className={classes.LandingPage}>
            <div style={{paddingLeft: '20px'}}>
                <Header/>
            </div>
            <div className={classes.InvoiceList}>
                Invoice List
            </div>
            <div className={classes.InvoiceTable}>
                <div style={{ paddingLeft: '19px' }}>
                    <Bar/>
                </div>
                <div>
                    <DataTable/>
                </div>
            </div>
        </div>
    )
}

export default LandingPage;
