        // left: '30px',
        // width: '90%',
        // height: '759.991px',
        // border: '10px solid black',
        // border: '10px solid black',
    },
    ToolBar: {
        // top: '2s0%',
        display: 'flex',
        // width: '1603.620px',
        // width: '80%',
        // height: 600, 
        width: 1553.620,
        position: 'static',
        background: '#273D49CC',
        boxShadow: 'none', 
        borderBottom: 'none',
        borderTopLeftRadius: 10,
        borderTopRightRadius: 10,
    },
    DataTable: {
        // width: '99%',
        // border: '10px solid black',
        paddingLeft: '20px',
        // padding
        borderBottomLeftRadius: 10,
        borderBottomRightRadius: 10,
    },
    tableHeading: {
        backgroundColor: '#273D49CC',
        color: '#97A1A9',
        fontSize: '15px',
        borderBottom: 'none',
        opacity: '100%',
        
    },
    tableBody: {
        "&:nth-of-type(odd)": {
            backgroundColor: '#273D49CC',
        },
        "&:nth-of-type(even)": {
            backgroundColor: "#283A46"
        },
        color: '#FFFFFF',
        fontSize: '16px',
    },
    tableRow: {
        color: '#FFFFFF',
        borderBottom: 'none',
    },
    PredictButton: {
        color: '#FFFFFF',
        background: '#97A1A9',
        font: 'normal normal normal 20px/24px Ubuntu',
        // fontFamily: 'normal',
        textTransform: 'none',
        paddingLeft: '10px',
        paddingRight: '10px',
    },
    ViewCorrespondence: {
        color: '#97A1A9',
        background: 'transparent',
        border: '1px solid #97A1A9',
        borderRadius: 10,
        textTransform: 'none',
    },
}));

